import React, {useState} from 'react';
import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
import axios from 'axios';

import NavBar from './NavBar'
import Routines from './Routines'
import Activities from './Activities'
import Register from './Register';
import Home from './Home'


const BASE_URL = 'http://fitnesstrac-kr.herokuapp.com/api/routines'

let currentUser;

const App = (props) => {

    const [currentUser, setCurrentUser] = useState('')
    const [navStatus, setNavStatus] = useState(false) 
    // const [searchTerm, setSearchTerm] = useState('')


    return <div id ="app">
        <Router>
            <header>
                <div id="navs">

                    <div id='title'>Track . Fit</div>

                    <label htmlFor="search-bar"></label>
                    <input type="text" 
                            id="search-bar" 
                            name="search-bar" 
                            placeholder="Search" ></input>
                    <a className="hamburger" onClick={() => setNavStatus(!navStatus)} ><i className="material-icons">menu</i></a>
                </div>
            </header>
            <div id="header-border-bar"></div>
            <main>
                <section id="lefty"></section>
                <section id="content-main">
                    <Route exact path='/Home' render={() => <Home />} />
                    <Route exact path='/Register' render={() => <Register />} />
                    {/* <Routines /> */}
                    {/* <Register /> */}

                </section>
                <section id="sidenav">

                    {navStatus ? <NavBar /> : ''}

                </section>

            </main>
        </Router>
    </div>

}

export default App;
export {currentUser}
