import React from 'react'
import { useState } from 'react'

async function getActivities() {
    let { data } = await axios.get(
      'http://fitnesstrac-kr.herokuapp.com/api/activities'
    )
    return data
  }

const Activities = () => {
    const [activities, setActivities] = useState([])

    const [page, setPage] = useState(0) // load 10 per page

    useEffect(() => {
        // async function required for awaiting the routes
        async function getAllActivities() {
          let data = await getRoutines()
          setRoutines(data) // when we have the data, set it in our components variable
        }

        // call the inner function once to start the process
        getAllRoutines()

        // console.log('USE EFFECT RUNS ONCE')
      }, [])

    return(
        <div>
            <h1>Activities</h1>
            <p>These are all available activities.  Don't see an activity you'd like to add a routine?  Create one!  Click create new activity to get started!</p>
            <ul>
                <li>Activity 1</li>
                <li>Activity Two</li>
                <li>Activity Tres</li>
            </ul>
        </div>
    )
}

export default Activities
