import React, {useState} from 'react';
import {BrowserRouter as Router, Route, Link} from "react-router-dom";
// import './userroutine.css';
import axios from 'axios';


const BASE_URL = 'http://fitnesstrac-kr.herokuapp.com/api'

const UserRoutine = ({
    name, goal, publicRoutine, currentUser, id
}) => {

    let [editable, setEditable] = useState(false);

    async function deleteRoutine(routine) {
        let response = await axios.delete(BASE_URL + '/routines/' + routine.id,
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem('currentUserToken')
            }
        });
        console.log(response.data);
    }

    function editRoutine() {
        newRoutine = {
            newRoutine: {
                name: newName,
                goal: newGoal,
                isPublic: newPublicRoutine
            }
        }
        axios.patch(BASE_URL + "/routines/" + id, 
            newRoutines,
            {
                headers: {
                    Authorization: "Bearer " + localStorage.getItem('currentUserToken')
                }
            });
        setEditable(false);
    }

    let newName = '';
    let newGoal = '';
    let newPublicRoutine = false;

    let newRoutine = {};

    return <><div className="user-routine">
                <div className="routine-labels">
                { editable ? <> 
                        <div id="routine-name">
                            <label htmlFor="new-routine-name">New Routine Name</label>
                            <input type="text" name="new-routine-name" id="input-routine-name" placeholder="New Routine Name"
                            onChange={(event) => {newName = event.target.value}}></input>
                        </div>
                        <div id="routine-goal">
                            <label htmlFor="new-routine-goal">New Routine Goal</label>
                            <textarea name="new-routine-goal" id="input-routine-goal" placeholder="New Goal"
                            onChange={(event) => {newGoal = event.target.value}}></textarea>
                        </div>
                        <div id="public-box">
                            <label htmlFor="new-routine-public">Make this Routine public?</label>
                            <input type="checkbox" name="new-routine-public" id="input-routine-public" value="public"
                            onChange={(event) => {newPublicRoutine = event.target.checked}}></input>
                        </div>
                </> : <>

                    <div className="routine-name">{name}</div>
                    <div className="routine-goal">{goal}</div>
                    <div className="routine-public">{publicRoutine ? "This routine is currently public." : "This routine is currently private."}</div>
                </> }
                </div>
        </div>
        <div className="routine-buttons">
            {localStorage.getItem("currentUser") ?
                <>
                { (localStorage.getItem("currentUser") === creatorName) ? 
                    <>
                        {editable ? 
                            <><button className="edit-routine-button" 
                                onClick={() => {
                                    editRoutine();
                                }}>Save Changes</button>
                            <button className="edit-routine-button" onClick={() => {setEditable(false)}}>Cancel Changes</button>
                            </> : 
                            <button className="edit-routine-button" 
                                onClick={() => {setEditable(true)}}>Edit Routine</button>
                        }

                        <button className="delete--button"
                            onClick={() => {deletePost(id)}}>Delete Post</button> 
                    </> :
                    <Link to="/NewMessage" className="reply-button" style={{textDecoration: 'none'}}>Reply</Link> 
                }
                </> : ''
            }
        </div>
    </>

}

export default UserRoutine;
