import React, { useState, useEffect} from 'react';
import './myroutines.css';
import axios from 'axios';
import UserRoutine from './UserRoutine';

const BASE_URL = 'http://fitnesstrac-kr.herokuapp.com/api';


const MyRoutines = ({currentUser}) => {
    if (currentUser === '') {
    return (<h1>Please login.</h1>)
    } 

    const [posts, setRoutines] = useState([]);

    async function getRoutines() {
        let data = (await axios.get(BASE_URL + '/posts')).data
        return data.data.posts;
    }

    useEffect ( async () =>{
        setRoutines(await getRoutines());
    }, [])

    const postMatches = function(post, term) {
        if (post.author.username.includes(term)){
                return true;
            } else {
                return false;
            }
    }

    const filteredRoutines = posts.filter(post => postMatches(post, localStorage.getItem('currentUser')));

    return <>
    {filteredPosts.map( (routine) => {
        return <UserRoutine
            title={routine.title}
            description={routine.description}
            price={routine.price}
            seller={routine.author.username}
            location={routine.location}
            deliverable={routine.willDeliver}
            currentUser={currentUser}
            id={routine.id}
        />})
    }
</>

}

export default MyPosts;
