import React, {useState} from 'react';
import {BrowserRouter as Router, Route, Link} from "react-router-dom";
import './userroutine.css';
import axios from 'axios';


const BASE_URL = 'http://fitnesstrac-kr.herokuapp.com/api';



const UserRoutine = ({
    id, 
}) => {

    let [editable, setEditable] = useState(false);

    let token = localStorage.getItem('currentUserToken')

    async function deleteRoutine(id) {
        let response = await axios.delete(BASE_URL + '/routines/' + id,
        {
            headers: {
                Authorization: 'Bearer ' + token
            }
        });

    }

    function editRoutine() {
        newRoutine = {
            routine: {
                title: newTitle,
                description: newDescription,
                location: newLocation,
                price: newPrice,
                willDeliver: newDeliverable
            }
        }
        axios.patch(BASE_URL + "/routines/" + id, 
            newRoutine,
            {
                headers: {
                    Authorization: "Bearer " + localStorage.getItem('currentUserToken')
                }
            });
        setEditable(false);
    }

    let newTitle = '';
    let newPrice = '';
    let newLocation = '';
    let newDescription = '';
    let newDeliverable = false;

    let newRoutine = {};

    return <><div className="user-routine">
                <div className="routine-labels">
                { editable ? <> 
                        <div id="routine-title">
                            <label htmlFor="new-routine-title">New Routine Title</label>
                            <input type="text" name="new-routine-title" id="input-routine-title" placeholder="New Routine Title"
                            onChange={(event) => {newTitle = event.target.value}}></input>
                        </div>
                        <div id="routine-price">
                            <label htmlFor="new-routine-price">Price</label>
                            <input type="text" name="new-routine-price" id="input-routine-price" placeholder="Price"
                            onChange={(event) => {newPrice = event.target.value}}></input>
                        </div>
                        <div id="routine-location">
                            <label htmlFor="new-routine-location">Location</label>
                            <input type="text" name="new-routine-location" id="input-routine-location" placeholder="Location"
                            onChange={(event) => {newLocation = event.target.value}}></input>
                        </div>
                        <div id="delivery-box">
                            <label htmlFor="new-routine-deliverable">Willing to Deliver?</label>
                            <input type="checkbox" name="new-routine-deliverable" id="input-routine-deliverable" value="deliverable"
                            onChange={(event) => {newDeliverable = event.target.checked}}></input>
                        </div>
                        <div id="routine-description">
                            <label htmlFor="new-routine-description">Description</label>
                            <textarea name="new-routine-description" id="input-routine-description" placeholder="Description"
                            onChange={(event) => {newDescription = event.target.value}}></textarea>
                        </div>
                </> : <>

                    <div className="routine-title">{title}</div>
                    <div className="routine-price">{price}</div>
                    <div className="routine-seller">{seller}</div>
                    <div className="routine-location">{location}</div>
                    <div className="routine-deliverable">{deliverable ? "Willing to Deliver" : "Pickup Only"}</div>
                    <div className="routine-description">{description}</div>
                </> }
                </div>
        </div>
        <div className="routine-buttons">
            {localStorage.getItem("currentUser") ?
                <>
                { (localStorage.getItem("currentUser") === seller) ? 
                    <>
                        {editable ? 
                            <><button className="edit-routine-button" 
                                onClick={() => {
                                    editRoutine();
                                }}>Save Changes</button>
                            <button className="edit-routine-button" onClick={() => {setEditable(false)}}>Cancel Changes</button>
                            </> : 
                            <button className="edit-routine-button" 
                                onClick={() => {setEditable(true)}}>Edit Routine</button>
                        }

                        <button className="delete-routine-button"
                            onClick={() => {deleteRoutine(id)}}>Delete Routine</button> 
                    </> :
                    '' 
                }
                </> : ''
            }
        </div>
    </>

}

export default UserRoutine;
