import React, { useState, useEffect } from 'react'
import axios from 'axios'
import './routines.css'

const BASE_URL = 'http://fitnesstrac-kr.herokuapp.com/api';

const Home = ({currentUser, searchTerm}) => {

    const [routines, setRoutines] = useState([]);

    async function getRoutines() {
        let data = (await axios.get(BASE_URL + '/routines')).data
        return data.routines;
    }

    useEffect ( async () =>{
        setRoutines(await getRoutines());
    }, [])

    // useEffect ( () => {

        const routineMatches = function(routine, term) {
            if (routine.title.includes(term) || routine.description.includes(term) ||
                routine.price.includes(term) || routine.author.username.includes(term)){
                    return true;
                } else {
                    return false;
                }
        }

        const filteredroutines = routines.filter(routine => routineMatches(routine, searchTerm));
        const routinesToDisplay = searchTerm.length ? filteredRoutines : routines;
    // }, [])

    return <>
    { localStorage.getItem('currentUser') ? 
        <>
            {routinesToDisplay.map( (routine) => {
                return <UserRoutine
                    title={routine.title}
                    description={routine.description}
                    price={routine.price}
                    seller={routine.author.username}
                    location={routine.location}
                    deliverable={routine.willDeliver}
                    currentUser={localStorage.getItem('currentUser')}
                    key={routine._id}
                    id={routine._id}
                />})
            }
        </> :
        <>
            {routinesToDisplay.map( (routine) => {
                return <UserRoutine
                    title={routine.title}
                    description={routine.description}
                    price={routine.price}
                    seller={routine.author.username}
                    location={routine.location}
                    deliverable={routine.willDeliver}
                    currentUser={localStorage.getItem('currentUser')}
                    key={routine.id}
                />})
            }
        </>
    }
    </>

}

export default Home
