import React, { useState, useEffect} from 'react';
import './myroutines.css';
import axios from 'axios';
import UserRoutine from './UserRoutine';

const BASE_URL = 'http://fitnesstrac-kr.herokuapp.com/api';


const MyRoutines = ({currentUser}) => {
    if (currentUser === '') {
    return (<h1>Please login.</h1>)
    } 

    const [myRoutines, setMyRoutines] = useState([]);

    async function getRoutines() {
        let {data} = (await axios.get(BASE_URL + '/routines'))
        console.log(data)
        return data
    }

    useEffect ( async () =>{
        let data = await getRoutines();
        setMyRoutines(data)
    }, [])

    const routineMatches = function(routine, term) {
        if (routine.creatorName.includes(term)){
                return true;
            } else {
                return false;
            }
    }

    const filteredRoutines = myRoutines.filter(routine => routineMatches(routine, localStorage.getItem('currentUser')));

    return <>
    {filteredRoutines.map( (routine) => {
        return <UserRoutine
            name={routine.name}
            goal={routine.goal}
            publicRoutine={routine.isPublic}
            currentUser={currentUser}
            key={routine.id}
            creatorName={routine.creatorName}
        />})
    }
</>

}

export default MyRoutines;
